from buildbot.steps.shell import ShellCommand
from buildbot.process.buildstep import LogLineObserver
from autobuilder.config import *
import os

class BuildEclipsePlugin(ShellCommand):
    haltOnFailure = False 
    flunkOnFailure = True 
    name = "Building Eclipse Plugin" 
    def __init__(self, factory, argdict=None, **kwargs):
        self._pendingLogObservers = []
        self.factory = factory
        self.branch = None
        for k, v in argdict.iteritems():
            setattr(self, k, v)
        self.description = "Building Eclipse Plugin"
        # Timeout needs to be passed to LoggingBuildStep as a kwarg
        self.timeout = 100000
        kwargs['timeout']=self.timeout
        self.slaveworkdir=os.path.join(os.path.join(YOCTO_ABBASE, "yocto-slave"))
        for k, v in argdict.iteritems():
            setattr(self, k, v)
        ShellCommand.__init__(self, **kwargs)

    def start(self):
        buildername=self.getProperty("buildername")
        self.branch=self.getProperty("branch_eclipse-poky")
        eclipsehome=self.slaveworkdir + "/" + buildername 
        self.command='cd eclipse-poky/scripts/; ./setup.sh; ECLIPSE_HOME="' + eclipsehome + '/build/eclipse-poky/scripts/eclipse" ./build.sh ' + self.branch + ' development'
        ShellCommand.start(self)

