from buildbot.steps.shell import ShellCommand
from buildbot.process.buildstep import LogLineObserver
from autobuilder.config import *
import os
from twisted.python import log

class BuildEclipsePlugin(ShellCommand):
    """
    Build step to run the Yocto Project Eclipse plugin builder.
    """
    haltOnFailure = False
    flunkOnFailure = True
    name = "Building Eclipse Plugin"
    def __init__(self, factory, argdict=None, **kwargs):
        """
        argdict contents:
            N/A
        """

        self._pendingLogObservers = []
        self.factory = factory
        self.eclipsedir = "eclipse-poky"
        for k, v in argdict.iteritems():
            setattr(self, k, v)
        self.description = "Building Eclipse Plugin"
        # Timeout needs to be passed to LoggingBuildStep as a kwarg
        self.timeout = 100000
        kwargs['timeout']=self.timeout
        self.workerworkdir=os.path.join(os.path.join(YOCTO_ABBASE, "yocto-worker"))
        for k, v in argdict.iteritems():
            setattr(self, k, v)
        ShellCommand.__init__(self, **kwargs)

    def start(self):
        buildername=self.getProperty("buildername")
        oldbuildername=buildername.replace("plugin", "poky")
        branch=self.getProperty("branch")
        eclipsehome=self.workerworkdir + "/" + buildername + '/build'
        self.command='cd ' + eclipsehome + '/scripts/; ./setup.sh; ECLIPSE_HOME="' + eclipsehome + '/scripts/eclipse" ./build.sh ' + branch + ' development'
        ShellCommand.start(self)

