from buildbot.steps.shell import ShellCommand
from buildbot.status import progress
from buildbot.status.results import SUCCESS, FAILURE, WARNINGS
from buildbot.process.properties import WithProperties
from twisted.python import log
from autobuilder.config import *

class GetLayerVersion(ShellCommand):
    haltOnFailure = False
    flunkOnFailure = False
    name = "GetLayerVersion"
    def __init__(self, factory, layerversion=None, workdir='build', argdict=None, **kwargs):
        for k, v in argdict.iteritems():
            setattr(self, k, v)
        self.description = "Get Layer Version"
        self.layerversion = layerversion
        self.workdir=workdir
        self.workerworkdir=os.path.join(os.path.join(YOCTO_ABBASE, "yocto-worker"))
        for k, v in argdict.iteritems():
            setattr(self, k, v)
        ShellCommand.__init__(self, **kwargs)

    def start(self):
        buildername=self.getProperty("buildername")
        log.msg(self.workdir)
        log.msg(self.layerversion)

        layerconf=self.workerworkdir + "/" + buildername + "/build/" + self.workdir + "/conf/layer.conf|grep LAYERVERSION_" + self.layerversion
        cmd = 'cat ' + layerconf
        self.command = cmd
        ShellCommand.start(self)

    def commandComplete(self, cmd):
        result = cmd.logs['stdio'].getText()
        layerv= result.replace("LAYERVERSION_" + self.layerversion, "").replace("=","").replace(' ','').replace('"','').replace("'",'').strip()
        if cmd.didFail():
           layerv = "1"
        self.setProperty('layerversion_' + self.layerversion, layerv, "Setting Layer Version")
        self.finished(SUCCESS)

    def getText(self, cmd, results):
        return ShellCommand.getText(self, cmd, results)
