from buildbot.steps.shell import ShellCommand
from twisted.python import log
from autobuilder.config import *

class PrepPkgIndex(ShellCommand):
    """
    Build step to ?????? beth confirm
    """
    haltOnFailure = False
    flunkOnFailure = True
    name = "PrepPkgIndex"
    def __init__(self, factory, argdict=None, **kwargs):
        """
        argdict contents:
            N/A
        """
        for k, v in argdict.iteritems():
            setattr(self, k, v)
        self.description = "Prepping to create pkg index"
        # Timeout needs to be passed to LoggingBuildStep as a kwarg
        for k, v in argdict.iteritems():
            setattr(self, k, v)
        self.timeout = 100000
        self.workdir="build/build/tmp/deploy/"
        kwargs['workdir']=self.workdir
        kwargs['timeout']=self.timeout
        ShellCommand.__init__(self, **kwargs)

    def start(self):
        import os
        DEST=self.getProperty('DEST')
        layerversion = self.getProperty("layerversion_core")
        IPK_PUBLISH_DIR = os.environ.get("IPK_PUBLISH_DIR")
        if layerversion and int(layerversion) < 4:
            command = "rm -rf ipk; cp -R " + DEST + "/" + IPK_PUBLISH_DIR + " ipk;"
        else:
            command = "rm -rf ipk; ln -s " + DEST + "/" + IPK_PUBLISH_DIR + " ipk;"
        self.command = command
        ShellCommand.start(self)

    def describe(self, done=False):
        description = ShellCommand.describe(self,done)
        return description
