from buildbot.steps.shell import ShellCommand
from buildbot.status.results import SUCCESS, WARNINGS, FAILURE, SKIPPED, \
     EXCEPTION, RETRY, worst_status
from autobuilder.config import *
import os

class SyncPersistDB(ShellCommand):
    haltOnFailure = False
    flunkOnFailure = True
    name = "syncing persistdb"
    def __init__(self, factory, argdict=None, **kwargs):
        self._pendingLogObservers = []
        self.factory = factory
        self.branch = None
        self.distro = "poky"
        self.commit = False
        for k, v in argdict.iteritems():
            setattr(self, k, v)
        self.description = "Syncing Persist DB"
        # Timeout needs to be passed to LoggingBuildStep as a kwarg
        self.timeout = 100000
        kwargs['timeout']=self.timeout
        self.workerworkdir=os.path.join(os.path.join(YOCTO_ABBASE, "yocto-worker"))
        for k, v in argdict.iteritems():
            setattr(self, k, v)
        ShellCommand.__init__(self, **kwargs)

    def start(self):
        self.buildername=self.getProperty("buildername")
        self.branch=self.getProperty("branch_poky")
        self.persistrepo=os.environ.get('PERSISTDB_DIR') + "/" + \
                         self.buildername + "/" + self.branch + "/" + \
                         self.distro
        if self.getProperty("repourl_poky") == "git://git.yoctoproject.org/poky" and \
           os.environ.get('MAINTAIN_PERSISTDB') == "True" and \
           self.branch=="master":
            self.command = "mkdir -p ./build/tmp/cache; if [ -b "+ self.persistrepo + "/bb_persist_data.sqlite3 ]; then cp -R " + \
                           self.persistrepo + " ./build/tmp/cache; fi ;"
            if str(self.commit) == 'True':
                self.command = "if [ ! -d "+ self.persistrepo +" ]; then mkdir -p " + \
                                self.persistrepo + "; fi ; "

                self.command = self.command + "rsync -av ./build/tmp/cache " + self.persistrepo
        else:
            self.command = "echo 'We do not sync the persistdb for builds \n that are not from the main poky repo \n or MAINTAIN_PERSISTDB is set to False'"
        ShellCommand.start(self)


