class Download(object):
    def __init__(self):
        self._build_conf = None
        self._main = None
        self._md5sum = None
        self._mirror = None
        
    @property
    def build_conf(self):
        return self._build_conf
        
    @build_conf.setter
    def build_conf(self, build_conf):
        self._build_conf = build_conf
        
    @property
    def main(self):
        return self._main
        
    @main.setter
    def main(self, main):
        self._main = main
        
    @property
    def md5sum(self):
        return self._md5sum
        
    @md5sum.setter
    def md5sum(self, md5sum):
        self._md5sum = md5sum
        
    @property
    def mirror(self):
        return self._mirror
        
    @mirror.setter
    def mirror(self, mirror):
        self._mirror = mirror
